  else {
    if (upper!=0.0) upper = upper+upperMargin*range;
    if (lower!=0.0) lower = lower-lowerMargin*range;
  }
  this.minimumAxisValue=new Double(lower);
  this.maximumAxisValue=new Double(upper);

